package org.example;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifference {
    public static char findTheDifference(String s, String t) {
        Map<Character, Integer> firstStrMap = new HashMap<>();
        Map<Character, Integer> secondStrMap = new HashMap<>();
        for (Character c : s.toCharArray()) {
            firstStrMap.put(c, firstStrMap.getOrDefault(c, 0) + 1);
        }

        for (Character c : t.toCharArray()) {
            secondStrMap.put(c, secondStrMap.getOrDefault(c, 0) + 1);
        }

        for (Map.Entry<Character, Integer> entry : secondStrMap.entrySet())  {
            Character secondStrMapKey = entry.getKey();
            Integer secondStrMapValue = entry.getValue();

            if (!firstStrMap.containsKey(secondStrMapKey) || firstStrMap.get(secondStrMapKey) != secondStrMapValue) {
                return secondStrMapKey;
            }
        }

        // 0 in ASCII-Code is null
        return 0;
    }

    public static void main(String[] args) {
        System.out.println(findTheDifference("abcd", "abcde"));
        System.out.println(findTheDifference("", "y"));
        System.out.println(findTheDifference("", ""));
    }
}
